import React, { useState } from 'react';
import EditGuestModal from './EditGuestModal';
import EditReservationModal from './EditReservationModal';
import axios from 'axios';
import './SearchGuestPage.css';

const SearchGuestPage = () => {
  const [formData, setFormData] = useState({
    id: '',
    name: '',
    kanaName: '',
    phone: ''
  });
  const [results, setResults] = useState([]);
  const [error, setError] = useState(null);
  const [modalData, setModalData] = useState(null);
  const [modalType, setModalType] = useState(null); // 'guest' または 'reservation'
  const [isModalOpen, setModalOpen] = useState(false);
  const [showOnlyUncheckedIn, setShowOnlyUncheckedIn] = useState(false);

  // type: 'guest' または 'reservation'
  const handleEditClick = (data, type) => {
    setModalData(data);
    setModalType(type);
    setModalOpen(true);
  };

  const handleCloseModal = () => {
    setModalOpen(false);
    setModalData(null);
    setModalType(null);
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSearch = async () => {
    try {
      const params = new URLSearchParams();
      Object.entries(formData).forEach(([key, value]) => {
        if (value) params.append(key, value);
      });

      const response = await axios.get(`http://localhost:8080/searchGuest?${params.toString()}`);
      setResults(response.data);
      setError(null);
    } catch (err) {
      setError('検索に失敗しました');
      setResults([]);
    }
  };

  const handleToggleUnchecked = () => {
    setShowOnlyUncheckedIn(!showOnlyUncheckedIn);
  };

  return (
    <div className="search-container">
      <h2 className="search-title">宿泊者単一検索</h2>
      <div className="search-form">
        <input name="id" placeholder="ID" value={formData.id} onChange={handleChange} />
        <input name="name" placeholder="名前" value={formData.name} onChange={handleChange} />
        <input name="kanaName" placeholder="ふりがな" value={formData.kanaName} onChange={handleChange} />
        <input name="phone" placeholder="電話番号" value={formData.phone} onChange={handleChange} />
        <button onClick={handleSearch}>検索</button>
      </div>
      <div className="filter-options">
        <label>
          <input
            type="checkbox"
            checked={showOnlyUncheckedIn}
            onChange={handleToggleUnchecked}
          />
          未チェックインの予約のみ表示
        </label>
      </div>
      {error && <p className="error-message">{error}</p>}
      <div className="results">
        {results.map((item, index) => (
          <div key={index} className="guest-result-group">
            {/* 宿泊者情報 */}
            <div className="guest-info-card">
              <h3>宿泊者情報</h3>
              <p><strong>名前:</strong> {item.guest.name} ({item.guest.kanaName})</p>
              <p><strong>電話番号:</strong> {item.guest.phone}</p>
              <p><strong>地域:</strong> {item.guest.region}</p>
              <p><strong>性別:</strong> {item.guest.gender}</p>
              <p><strong>年齢:</strong> {item.guest.age}</p>
              <p><strong>メール:</strong> {item.guest.email}</p>
              <button className="edit-button" onClick={() => handleEditClick(item.guest, 'guest')}>編集</button>
            </div>
            {/* 予約情報+プラン情報をまとめて表示 */}
            {item.reservations
              .filter(reservation => !showOnlyUncheckedIn || reservation.status === "NOT_CHECKED_IN")
              .map((reservation, i) => (
                <div key={i} className="reservation-info-card">
                  <h4>予約情報</h4>
                  <p><strong>チェックイン日:</strong> {reservation.checkInDate}</p>
                  <p><strong>ステータス:</strong> {reservation.status}</p>
                  <p><strong>合計金額:</strong> ¥{reservation.totalPrice}</p>
                  <p><strong>宿泊日数:</strong> {reservation.stayDays}日</p>
                  <p><strong>メモ:</strong> {reservation.memo}</p>
                  <p><strong>作成日時:</strong> {reservation.createdAt}</p>

                  {/* 対応するプラン情報（同じindexで対応） */}
                  {item.bookings && item.bookings[i] && (
                    <div className="plan-info">
                      <h5>プラン</h5>
                      <p><strong>プラン名:</strong> {item.bookings[i].name}</p>
                      <p><strong>料金:</strong> ¥{item.bookings[i].price}</p>
                      <p><strong>説明:</strong> {item.bookings[i].description}</p>
                    </div>
                  )}

                  <button className="edit-button" onClick={() => handleEditClick(reservation, 'reservation')}>編集</button>
                </div>
              ))}
          </div>
        ))}
      </div>
      <a href="/" className="back-to-home">Homeに戻る</a>
      {isModalOpen && modalType === 'guest' && (
        <EditGuestModal
          guest={modalData}
          onClose={handleCloseModal}
        />
      )}
      {isModalOpen && modalType === 'reservation' && (
        <EditReservationModal
          reservation={modalData}
          onClose={handleCloseModal}
        />
      )}
    </div>
  );
};

export default SearchGuestPage;
