import React, { useState, useEffect } from 'react';

const EditGuestModal = ({ guest, onClose }) => {
  const [formData, setFormData] = useState({
    name: '',
    kanaName: '',
    phone: '',
    email: '',
    gender: '',
    age: '',
    region: ''
  });

  useEffect(() => {
    if (!guest) return;
    const target = guest.guest ? guest.guest : guest;
    setFormData({
      name: target.name || '',
      kanaName: target.kanaName || '',
      phone: target.phone || '',
      email: target.email || '',
      gender: target.gender || '',
      age: target.age || '',
      region: target.region || ''
    });
  }, [guest]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Submit logic here
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <h2>宿泊者情報編集</h2>
        <form onSubmit={handleSubmit}>
          <label>
            名前:
            <input name="name" value={formData.name} onChange={handleChange} />
          </label>
          <label>
            ふりがな:
            <input name="kanaName" value={formData.kanaName} onChange={handleChange} />
          </label>
          <label>
            電話番号:
            <input name="phone" value={formData.phone} onChange={handleChange} />
          </label>
          <label>
            メール:
            <input name="email" value={formData.email} onChange={handleChange} />
          </label>
          <label>
            性別:
            <input name="gender" value={formData.gender} onChange={handleChange} />
          </label>
          <label>
            年齢:
            <input name="age" value={formData.age} onChange={handleChange} />
          </label>
          <label>
            地域:
            <input name="region" value={formData.region} onChange={handleChange} />
          </label>
          <button type="submit">保存</button>
          <button type="button" onClick={onClose}>閉じる</button>
        </form>
      </div>
    </div>
  );
};

export default EditGuestModal;
