import React, { useState, useEffect } from 'react';
import './EditGuestModal.css';

const EditGuestModal = ({ guest, onClose, onSubmit }) => {
  const [formData, setFormData] = useState({ ...guest });

  useEffect(() => {
    setFormData({ ...guest }); // モーダル開くたびに初期化
  }, [guest]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);  // 外部から編集処理呼び出し
    onClose();            // モーダルを閉じる
  };

  return (
    <div className="modal-backdrop">
      <div className="modal">
        <h2 className="modal-title">宿泊者情報の編集</h2>
        <form onSubmit={handleSubmit} className="modal-form">
          <label>
            氏名
            <input name="name" value={formData.name || ''} onChange={handleChange} required />
          </label>
          <label>
            ふりがな
            <input name="kanaName" value={formData.kanaName || ''} onChange={handleChange} />
          </label>
          <label>
            電話番号
            <input name="phone" value={formData.phone || ''} onChange={handleChange} />
          </label>
          <label>
            メールアドレス
            <input name="email" value={formData.email || ''} onChange={handleChange} />
          </label>
          {/* 必要に応じて他の項目（age, regionなど）も追加可能 */}

          <div className="modal-actions">
            <button type="submit" className="btn-save">保存</button>
            <button type="button" onClick={onClose} className="btn-cancel">キャンセル</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default EditGuestModal;
