import React, { useState, useEffect } from 'react';
import './EditGuestModal.css';

const EditGuestModal = ({ guest, onClose, onSubmit }) => {
  const [formData, setFormData] = useState({
    name: '',
    kanaName: '',
    phone: '',
    email: '',
    gender: '',
    age: '',
    region: ''
  });

  useEffect(() => {
    if (guest?.guest) {
      const g = guest.guest;
      setFormData({
        name: g.name || '',
        kanaName: g.kanaName || '',
        phone: g.phone || '',
        email: g.email || '',
        gender: g.gender || '',
        age: g.age || '',
        region: g.region || ''
      });
    }
  }, [guest]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = () => {
    onSubmit(formData);
    onClose();
  };

  return (
    <div className="modal-backdrop">
      <div className="modal">
        <h3 className="modal-title">宿泊者情報を編集</h3>
        <div className="modal-form">
          <label>名前
            <input name="name" value={formData.name} onChange={handleChange} />
          </label>
          <label>ふりがな
            <input name="kanaName" value={formData.kanaName} onChange={handleChange} />
          </label>
          <label>電話番号
            <input name="phone" value={formData.phone} onChange={handleChange} />
          </label>
          <label>メール
            <input name="email" value={formData.email} onChange={handleChange} />
          </label>
          <label>性別
            <input name="gender" value={formData.gender} onChange={handleChange} />
          </label>
          <label>年齢
            <input name="age" type="number" value={formData.age} onChange={handleChange} />
          </label>
          <label>地域
            <input name="region" value={formData.region} onChange={handleChange} />
          </label>
        </div>
        <div className="modal-actions">
          <button className="btn-save" onClick={handleSubmit}>保存</button>
          <button className="btn-cancel" onClick={onClose}>キャンセル</button>
        </div>
      </div>
    </div>
  );
};

export default EditGuestModal;
