import React, { useEffect, useState } from 'react';

function GuestList() {
  const [guests, setGuests] = useState([]);

  useEffect(() => {
    async function getGuests() {
      try {
        const response = await fetch('http://localhost:8080/guestList');
        const data = await response.json();
        setGuests(data);
      } catch (error) {
        console.error('ゲスト一覧の取得に失敗しました', error);
      }
    }

    getGuests();
  }, []);

  return (
    <div>
      <h2>宿泊者一覧</h2>
      {guests.map((item) => (
        <div key={item.guest.id} style={{ border: '1px solid #ccc', padding: '10px', marginBottom: '10px' }}>
          <h3>{item.guest.name}（{item.guest.phone}）</h3>
          <p>メール: {item.guest.email}</p>
          <p>地域: {item.guest.region}</p>
          <p>年齢: {item.guest.age} / 性別: {item.guest.gender}</p>

          {item.bookings.length > 0 && (
            <div>
              <strong>予約プラン:</strong>
              <ul>
                {item.bookings.map((booking) => (
                  <li key={booking.id}>{booking.name} - ¥{booking.price}</li>
                ))}
              </ul>
            </div>
          )}

          {item.reservations.length > 0 && (
            <div>
              <strong>予約情報:</strong>
              <ul>
                {item.reservations.map((res) => (
                  <li key={res.id}>
                    チェックイン日: {res.checkInDate}, 泊数: {res.stayDays}, 状態: {res.status}
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      ))}
    </div>
  );
}

export default GuestList;
