import React, { useState } from 'react';
import axios from 'axios';
import './SearchGuestPage.css';

const SearchGuestPage = () => {
  const [formData, setFormData] = useState({
    id: '',
    name: '',
    kanaName: '',
    phone: ''
  });
  const [results, setResults] = useState([]);
  const [error, setError] = useState(null);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSearch = async () => {
    try {
      const params = new URLSearchParams();
      Object.entries(formData).forEach(([key, value]) => {
        if (value) params.append(key, value);
      });

      const response = await axios.get(`http://localhost:8080/searchGuest?${params.toString()}`);
      setResults(response.data);
      setError(null);
    } catch (err) {
      setError('検索に失敗しました');
      setResults([]);
    }
  };

  return (
    <div className="search-container">
      <h2 className="search-title">宿泊者単一検索</h2>
      <div className="search-form">
        <input className="search-input" name="id" placeholder="ID" value={formData.id} onChange={handleChange} />
        <input className="search-input" name="name" placeholder="名前" value={formData.name} onChange={handleChange} />
        <input className="search-input" name="kanaName" placeholder="ふりがな" value={formData.kanaName} onChange={handleChange} />
        <input className="search-input" name="phone" placeholder="電話番号" value={formData.phone} onChange={handleChange} />
        <button className="search-button" onClick={handleSearch}>検索</button>
      </div>
      {error && <p className="search-error">{error}</p>}
      <div className="search-results">
        {results.map((item, index) => (
          <div className="result-card" key={index}>
            <p><strong>名前:</strong> {item.guest.name} ({item.guest.kanaName})</p>
            <p><strong>電話番号:</strong> {item.guest.phone}</p>
            <p><strong>地域:</strong> {item.guest.region}</p>
            <p><strong>年齢:</strong> {item.guest.age}</p>
            <p><strong>メール:</strong> {item.guest.email}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default SearchGuestPage;
